import flushPromises from 'flush-promises';
import * as serverLogout from '@opensrp/server-logout';
import toJson from 'enzyme-to-json';
import React from 'react';
import { CustomLogout } from '..';
import { Provider } from 'react-redux';
import { MemoryRouter } from 'react-router';
import { store } from '@opensrp/store';
import * as notifications from '@opensrp/notifications';
import { act } from 'react-dom/test-utils';
import { mountWithTranslations } from '../../../helpers/testUtils';
import { getOpenSRPUserInfo } from '@onaio/gatekeeper';
import { authenticateUser } from '@onaio/session-reducer';

jest.mock('@opensrp/notifications', () => ({
  __esModule: true,
  ...Object.assign({}, jest.requireActual('@opensrp/notifications')),
}));

jest.mock('../../../configs/env');

const jwtAccessToken =
  'eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJHZ0NjX3c0UG9Gd25vbThILXpQMEQ4UTc1ZjZ1LWdHLUJTZV9Xc1QxSkU0In0..AhC1rYONG37Er8YUw0OvEM6h3FqaFYFBN845kOZN2bFo8_x3kpaWuZ5qGGxh8LfPqnMsjnpkL4dXD_3E8uTvjBZBFIeLdck2RaYmxoPXK7j0lDnf4ia36oz2TKUVSBDijacNFdmxmVbyeddFcN6ZPluzO9bvgFkIqIEyCwrLLZEnZwsUdUlgfD4V_ebwkOcSH0z69AkQprZSRPksd5CsY8cPqqDYNRhjRBNqvBdcxtlPwv48Mtpau4rs3yucYKahscNolVAkE_FetEI0KenZdYV5g9N3VdneCsjW4DdZkcuZDrKaA6g64gBUyXEptRsL4wYPwup4_G5NU8vrD-L2cA';

describe('components/Logout', () => {
  afterEach(() => {
    jest.clearAllMocks();
  });

  beforeAll(() => {
    const { authenticated, user, extraData } = getOpenSRPUserInfo({
      oAuth2Data: {
        access_token: jwtAccessToken,
        state: 'opensrp',
        token_expires_at: '2017-07-13T20:30:59.000Z',
        token_type: 'bearer',
      },
    });
    store.dispatch(authenticateUser(authenticated, user, extraData));
  });

  it('renders logout component', () => {
    const wrapper = mountWithTranslations(
      <Provider store={store}>
        <MemoryRouter>
          <CustomLogout />
        </MemoryRouter>
      </Provider>
    );
    expect(toJson(wrapper.find('CustomLogout'))).toMatchSnapshot();
  });

  it('calls logout with correct params', async () => {
    const mock = jest.spyOn(serverLogout, 'logout');
    mock.mockRejectedValue('Logout failed');
    const payload = {
      headers: {
        accept: 'application/json',
        authorization: `Bearer ${jwtAccessToken}`,
        'content-type': 'application/json;charset=UTF-8',
      },
      method: 'GET',
    };
    const logoutURL = 'https://opensrp-stage.smartregister.org/opensrp/logout.do';
    const keycloakURL =
      'https://keycloak-stage.smartregister.org/auth/realms/opensrp-web-stage/protocol/openid-connect/logout';
    mountWithTranslations(
      <Provider store={store}>
        <MemoryRouter>
          <CustomLogout />
        </MemoryRouter>
      </Provider>
    );
    await flushPromises();
    expect(mock).toHaveBeenCalledWith(
      payload,
      keycloakURL,
      'http://localhost:3000',
      logoutURL,
      undefined
    );
  });

  it('handles logout failure correctly', async () => {
    jest.spyOn(serverLogout, 'logout').mockRejectedValueOnce(new Error('error'));
    const mockNotificationError = jest.spyOn(notifications, 'sendErrorNotification');
    mountWithTranslations(
      <Provider store={store}>
        <MemoryRouter>
          <CustomLogout />
        </MemoryRouter>
      </Provider>
    );
    await act(async () => {
      await flushPromises();
    });
    expect(mockNotificationError).toHaveBeenCalledWith(
      'There was a problem logging out. Please try again.'
    );
  });
});
